mapper.select:
SELECT COUNT(*) FROM BOARD
WHERE BNO = #{bno}
  AND BNORELEV = (SELECT BNORELEV FROM BOARD WHERE BNO = #{bno})

mapper.update:
UPDATE BOARD SET
FILEEXIST = #{fileExist}
WHERE BNO = #{bno}

mapper.select:
SELECT FNO, BNO, FNAME, RNAME, FSIZE FROM BOARDFILE
WHERE BNO = #{bno}
AND DELYN = 'N'

SELECT FNO, BNO, FNAME, RNAME, FSIZE FROM BOARDFILE
WHERE FNO = #{fno}
AND BNO = #{bno}

mapper.delete:
UPDATE BOARDFILE
SET DELYN = 'Y'
WHERE BNO = #{bno}

mapper.update:
UPDATE BOARDFILE
SET DELYN = 'N',
	ISNEW = #{isnew}
WHERE FNO = #{fno}

mapper.delete:
DELETE FROM BOARD

	WHERE BNOREREF = #{bnoreref}


    WHERE BNO = #{bno}

mapper.update:
UPDATE BOARD SET BNORESEQ=BNORESEQ+1
WHERE BNOREREF = #{bnoreref}
and BNORESEQ > #{bnoreseq}

mapper.select:
SELECT COUNT(*) FROM BOARD
WHERE BNO = #{bno}
AND BPASS = #{bpass}
