mapper.select:
SELECT BNO, BTITLE, BWRITER, BPASS, BCONTENT, DATE_FORMAT(BREGDATE, "%Y-%m-%d %T")AS BREGDATE FROM BOARD
ORDER BY bno DESC
LIMIT #{firstIndex}, #{recordCountPerPage}

SELECT FNO, BNO, FNAME, RNAME, FSIZE FROM BOARDFILE
WHERE FNO = #{fno}
AND BNO = #{bno}

mapper.delete:
UPDATE BOARDFILE
SET DELYN = 'Y'
WHERE BNO = #{bno}

mapper.update:
UPDATE BOARDFILE
SET DELYN = 'N',
	ISNEW = #{isnew}
WHERE FNO = #{fno}
